import React, {useState, useEffect} from "react";

function ProfileComponent() {
    constructor [show, setShow] = useState(true);

    const removeJobComponent = () => {
        setShow(!show);
    }

    return (
        <div>
           <button type="button" onClick={removeJobComponent}>Remove Job
                {show ? <JobComponent /> : null}
            </button>
        </div>
    )
}


function JobComponent() {
    const [job, setJob] = useState("Developer");

    useEffect(() => {
        if(job === "Developer"){
            setTimeout(() => {
                this.setState({job: "Frontend Developer"});
            },3000);
            console.log("componentDidMount: 마운트 실행");
        }
        else {
            console.log("componentDidUpdate: 컴포넌트 업데이트 완료");

            return() => {
                console.log("componentWillUnmount: 컴포넌트 마운트 해제, 컴포넌트 제거");
            }
        }
    }, [job]);



        return(
            <div>
                <h2>Job Title:
                    <span id="jobTitle"></span>
                </h2>
            </div>
        )

}
